INSERT INTO `wp_posts` VALUES('99', '1', '2015-06-15 00:02:50', '2015-06-15 00:02:50', '\n<section id=\"builder-section-1434326519021\" class=\"builder-section builder-section-first builder-section-gallery builder-section-next-text builder-gallery-columns-3 builder-gallery-captions-reveal builder-gallery-captions-light builder-gallery-aspect-square\" style=\"\">\n		<div class=\"builder-section-content\">\n				<div class=\"builder-gallery-item has-link\" onclick=\"window.location.href = \'http://michelleweidman.com/allison-halter-conflating-sissy-bounce-with-minimalism\';\">\n										<figure class=\"builder-gallery-image\" style=\"background-image: url(&#039;http://michelleweidman.com/wp-content/uploads/2015/06/flyer-flat.jpg&#039;);\"></figure>									<div class=\"builder-gallery-content\">\n				<div class=\"builder-gallery-content-inner\">\n										<h4 class=\"builder-gallery-title\">\n						Allison Halter: Conflating Sissy Bounce with Minimalism					</h4>\n														</div>\n			</div>\n					</div>\n				<div class=\"builder-gallery-item last-2\" onclick=\"return false;\">\n											</div>\n				<div class=\"builder-gallery-item last-3\" onclick=\"return false;\">\n											</div>\n			</div>\n	</section>\n\n<section id=\"builder-section-1434326531359\" class=\"builder-section builder-section-prev-gallery builder-section-text builder-section-last builder-text-columns-3\" style=\"\">\n		<div class=\"builder-section-content\">\n				<div class=\"builder-text-column builder-text-column-1\" id=\"builder-section-1434326531359-column-1\">\n														</div>\n				<div class=\"builder-text-column builder-text-column-2\" id=\"builder-section-1434326531359-column-2\">\n														</div>\n				<div class=\"builder-text-column builder-text-column-3\" id=\"builder-section-1434326531359-column-3\">\n														</div>\n			</div>\n	</section>\n', 'Interviews', '', 'publish', 'closed', 'closed', '', 'interviews', '', '', '2015-10-19 04:30:26', '2015-10-19 04:30:26',